import React from "react";
import { CellState, CellValue } from "../../types";
import "./Button.scss";

interface ButtonProps {
    row: number;
    col: number;
    state: CellState;
    value: CellValue;
    onClick(rowParam: number, colParam: number): (...args: any[]) => void;
    onContext(rowParam: number, colParam: number): (...args: any[]) => void;
}

const Button: React.FC<ButtonProps> = ({ row, col, state, value, onContext, onClick }) => {

    const renderContent = (): React.ReactNode => { // <!------ renderContent()
        if (state === CellState.visible) {
            if (value === CellValue.bomb) {
                return <span role='img' aria-label="bomb">üí£</span>
            } else if (value === CellValue.none) {
                return null;
            }
            return value;
        } else if (state === CellState.flagged) {
            return <span role='img' aria-label="flag">üè¥‚Äç‚ò†Ô∏è</span>
        }
        return null
    }; // <!------------------------------------------------- renderContent()

    return (
        <div className={`Button ${state === CellState.visible ? "visible" : ""} value-${value}`}
        onClick={onClick(row, col)}
        onContextMenu={onContext(row, col)} // handleRightClick()
        >
            {renderContent()}
        </div>
    );
};

export default Button;
